<script lang="ts">
	import '../app.scss';
	import NavButton from '../components/nav_button.svelte';
	import InfoButton from '../components/info_button.svelte';
	import EducationCard from '../components/education_card.svelte';
	import AchievementCard from '../components/achievement_card.svelte';
	import ProjectCard from '../components/project_card.svelte';
	import SkillCard from '../components/skill_card.svelte';
	import { onMount } from 'svelte';

	const sections = ['#home', '#about', '#education', '#achievements', '#skills', '#projects'];
	// I am so sorry!
	let nav_button_status: boolean[] = [true, false, false, false, false];
	let container: HTMLDivElement | null;
	let should_move_container = false;
	let is_closing = false;
	let last_touch_pos = 0;
	let is_nav_closed = true;

	onMount(() => {
		const nav = document.querySelector('nav');

		window.addEventListener('touchstart', (e) => {
			if (nav) {
				nav.style.transition = '0ms';
				last_touch_pos = e.touches[0].clientX;

				should_move_container =
					(last_touch_pos >= (window.visualViewport?.width ?? 650) * 0.8 && !is_nav_closed) ||
					(last_touch_pos <= (window.visualViewport?.width ?? 650) * 0.2 &&
						nav.style.right != '0px');
			}
		});

		window.addEventListener('touchmove', (e) => {
			is_closing = last_touch_pos > e.touches[0].clientX;

			if (nav) {
				last_touch_pos = e.touches[0].clientX;
				let visual_viewport_width = window.visualViewport?.width ?? 650;
				if (should_move_container && last_touch_pos <= visual_viewport_width) {
					let new_pos = visual_viewport_width - e.touches[0].clientX;
					nav.style.right = `${new_pos}px`;
				}
			}
		});

		window.addEventListener('touchend', () => {
			let viewport_size = window.visualViewport?.width ?? 650;
			if (nav && should_move_container) {
				let viewport_percentage = is_closing ? 0.6 : 0.2;

				if (last_touch_pos <= viewport_size * viewport_percentage) {
					nav.style.right = `${viewport_size * 1.2}px`;
					is_nav_closed = true;
				} else {
					is_nav_closed = false;
					nav.style.right = `0px`;
				}
				nav.style.transition = '300ms';

				should_move_container = false;
			}
		});

		container = document.querySelector('#content');

		container?.addEventListener('scroll', () => {
			sections.forEach((section, idx) => {
				let section_el = document.querySelector(section);
				const container_height = container?.getBoundingClientRect().height ?? 1080;
				const section_y = section_el?.getBoundingClientRect().y ?? 0;

				if (section_y <= container_height / 2) {
					nav_button_status = nav_button_status.map((_, index) => index == idx);
				}
			});
		});

		let scroll_reveal = ScrollReveal({ distance: '50px', origin: 'bottom', reset: true });

		scroll_reveal.reveal(
			'#about h1, #about img, #about p, #education h1, #achievements h1, #skills h1, #projects h1',
			{
				container: '#content',
				viewFactor: 0.3,
				duration: 500,
				scale: 0.3
			}
		);

		document.querySelectorAll('.education-card')?.forEach((card, idx) => {
			scroll_reveal.reveal(card, {
				container: '#content',
				viewFactor: 0.3,
				delay: 100 * idx,
				duration: 500,
				scale: 0.2
			});
		});

		document.querySelectorAll('.achievement-card')?.forEach((card, idx) => {
			scroll_reveal.reveal(card, {
				container: '#content',
				viewFactor: 0.3,
				delay: 100 * idx,
				duration: 500,
				scale: 0.2
			});
		});

		document.querySelectorAll('.skill-card')?.forEach((card, idx) => {
			scroll_reveal.reveal(card, {
				container: '#content',
				viewFactor: 0.3,
				delay: 100 * idx,
				duration: 500,
				scale: 0.2
			});
		});

		document.querySelectorAll('.project-card')?.forEach((card, idx) => {
			scroll_reveal.reveal(card, {
				container: '#content',
				viewFactor: 0.3,
				delay: 100 * idx,
				duration: 500,
				scale: 0.2
			});
		});
	});

	function show_nav() {
		let nav = document.querySelector('nav');
		if (nav) {
			nav.style.transition = '300ms';
			nav.style.right = '0px';
			is_nav_closed = false;
		}
	}

	function on_nav(nav_idx: number) {
		if (container) {
			let nav = document.querySelector('nav');
			if (nav) {
				let viewport_size = window.visualViewport?.width ?? 650;
				nav.style.transition = '300ms';
				nav.style.right = `${viewport_size * 1.2}px`;
				is_nav_closed = true;
			}

			let section = document.querySelector(sections[nav_idx]);
			if (section) {
				let offset_top = 0;
				if (nav_idx > 0) {
					offset_top = (section.offsetTop ?? 0) + section.offsetParent.offsetTop;
				}

				container.scrollTo({
					top: offset_top,
					behavior: 'smooth'
				});
			}
		}
	}
</script>

