<script defer lang="ts">
    import '../app.scss';
    import { onMount } from 'svelte';
    import RepoCard from '../components/repo-card.svelte';

    onMount(() => {
        let content_container = document.querySelector<HTMLDivElement>('.content-container');
        let sections = [
            document.querySelector('#Intro'),
            document.querySelector('#About'),
            document.querySelector('#Projects'),
            document.querySelector('#Skills'),
            document.querySelector('#Links')
        ];
        let nav = document.querySelector('nav');
        let nav_transition_title = document.querySelector<HTMLHeadElement>('#nav-transition-title');
        let nav_transition_divs = document.querySelectorAll('.nav-transition');
        let previous_nav_timer = 0;
        let previous_nav_timer2 = 0;

        for (let nav_link of nav.children) {
            nav_link.addEventListener('click', () => {
                window.scrollTo({ top: 0, left: 0 });
                clearTimeout(previous_nav_timer);
                clearTimeout(previous_nav_timer2);
                nav_transition_title.innerText = nav_link.getAttribute('data-nav');
                nav_transition_divs.forEach((el) => el.classList.add('active'));

                previous_nav_timer = setTimeout(() => {
                    for (let section of sections) {
                        section.classList.add('invisible');
                        document
                            .querySelectorAll('nav > button')
                            .forEach((button) => button.classList.remove('active'));
                    }
                    document
                        .querySelector(`#${nav_link.getAttribute('data-nav')}`)
                        .classList.remove('invisible');
                    nav_link.classList.add('active');
                }, 1300);

                previous_nav_timer2 = setTimeout(() => {
                    nav_transition_divs.forEach((el) => el.classList.remove('active'));
                }, 1300);
            });
        }
    });
</script>

